    "pi_claims.key_features.feature2": { en: "Dedicated Claims Manager", fr: "Gestionnaire de Sinistres Dédié" },
    "pi_claims.key_features.feature3": { en: "Real-time Claims Tracking", fr: "Suivi des Sinistres en Temps Réel" },
