    "quality_policy.commitment1": { en: "Reliable Service: Offer inspection and certification services in a reliable, transparent, safe and professional manner.", fr: "Service Fiable : Offrir des services d'inspection et de certification de manière fiable, transparente, sûre et professionnelle." },
    "quality_policy.commitment2": { en: "ISO 9001 Compliance: Meet the requirements of both customers and applicable legal and regulatory requirements.", fr: "Conformité ISO 9001 : Répondre aux exigences des clients et aux exigences légales et réglementaires applicables." },
    "quality_policy.commitment3": { en: "Impartial Performance: Ensure IMPARTIAL and INDEPENDENT performance of the Organization and its members.", fr: "Performance Impartiale : Assurer une performance IMPARTIALE et INDÉPENDANTE de l'Organisation et de ses membres." },
    "quality_policy.commitment4": { en: "Competent Staff: Keep inspection staff technically TRAINED and COMPETENT to carry out their activities.", fr: "Personnel Compétent : Maintenir le personnel d'inspection techniquement FORMÉ et COMPÉTENT pour exercer ses activités." },
